mapper.select:
select a.photo_id, a.des, a.rank, b.*  
from goodsphoto a inner join goods b 
on a.proid=b.proid

	
		and a.photo_id=#{photoId}
	
	
		and a.proid=#{proid}
	
	
		and a.rank=#{rank}
	
	
		and a.des like #{des}

		SELECT * FROM  (SELECT X.*, ROWNUM RN  
FROM (
		select a.photo_id, a.des, a.rank, b.*  
		from goodsphoto a inner join goods b 
		on a.proid=b.proid
		
			
				and a.photo_id=#{photoId}
			
			
				and a.proid=#{proid}
			
			
				and a.rank=#{rank}
			
			
				and a.des like #{des}
			
		)
		 X WHERE ROWNUM <=#{end})  WHERE RN >=#{start}
