mapper.select:
		select * from 
    		(select u.*, r.roleid, r.rolename, r.detial
     		from sys_user u 
        	left outer join user_link_role l on u.userid = l.userid
     		left outer join user_role r on l.roleid = r.roleid
     		left outer join area a on u.area_ID = a.area_ID 
     		order by u.userid desc)
  		
  			
  				username like #{name}
  			
  			
  				and roleid in
  				
  					#{i}
  				
  			
  			
  				and createDate >= #{before}
  			
  			
  				and createDate <= #{after}
  			
  			
  				and area_id = #{area}

		select * from(
			select a.*, rownum rn from 
		    (select u.*, r.roleid, r.rolename, r.detial
     		from sys_user u 
        	left outer join user_link_role l on u.userid = l.userid
     		left outer join user_role r on l.roleid = r.roleid
     		left outer join area a on u.area_ID = a.area_ID 
     		order by u.userid desc) a
		    
		     	
	  				username like #{name}
	  			
	  			
	  				and roleid in
	  				
	  					#{i}
	  				
	  			
	  			
	  				and createDate >= #{before}
	  			
	  			
	  				and createDate <= #{after}
	  			
	  			
  					and area_id = #{area}
  				
	  			
	  				and rownum <= #{end}
	  			
		    	  
		 ) where rn >= #{start}

		select * from (
       		select u.*, rownum rn from sys_user u 
		
			
	  				username like #{name}
	  			
	  			
	  				and createDate >= #{before}
	  			
	  			
	  				and createDate <= #{after}
	  			
	  			
  					and area_id = #{area}
  				
				
	  				and rownum <= #{end}
	  				
		 
		)where rn >= #{start}

		select count(u.userid) from sys_user u 
		
			
	  				username like #{name}
	  			
	  			
	  				and createDate >= #{before}
	  			
	  			
	  				and createDate <= #{after}
	  			
	  			
  					and area_id = #{area}
